mapper.select:
SELECT COUNT(*) FROM user_challenge uc
                         JOIN challenge c ON uc.challenge_id = c.ID
WHERE uc.user_id = #{userId}
  AND c.type = #{type}
  AND c.status IN ('RECRUITING', 'IN_PROGRESS')

mapper.update:
UPDATE challenge
SET participant_count = participant_count + 1
WHERE id = #{challengeId}

UPDATE challenge
SET status = #{status}
WHERE id = #{challengeId}
