mapper.select:
SELECT cr.id, u.id AS userId, us.nickname, cr.`rank`, cr.cumulative_point AS totalCoin, cr.month FROM ( SELECT * FROM challenge_coin_rank_snapshot WHERE month = #{month} ORDER BY `rank` ASC LIMIT 5 ) cr JOIN user u ON cr.user_id = u.id JOIN user_status us ON u.id = us.id
UNION ALL
SELECT cr.id, u.id AS userId, us.nickname, cr.`rank`, cr.cumulative_point AS totalCoin, cr.month FROM challenge_coin_rank_snapshot cr JOIN user u ON cr.user_id = u.id JOIN user_status us ON u.id = us.id WHERE cr.month = #{month} AND cr.user_id = #{userId}

mapper.update:
UPDATE challenge_coin_rank_snapshot
SET is_checked = 1
WHERE month = #{month} AND user_id = #{userId}
