mapper.update:
UPDATE coin
SET amount = amount - #{amount}
WHERE id = #{userId}

UPDATE coin
SET amount = amount + #{amount},
    cumulative_amount = cumulative_amount + #{amount},
    monthly_cumulative_amount = monthly_cumulative_amount + #{amount}
WHERE id = #{userId}

mapper.select:
SELECT COALESCE(monthly_cumulative_amount, 0)
FROM coin
WHERE id = #{userId}

SELECT DATE_FORMAT(updated_at, '%Y-%m-%d %H:%i:%s')
FROM coin
WHERE id = #{userId}

mapper.update:
UPDATE coin
SET amount = amount + #{amount},
    cumulative_amount = cumulative_amount + #{amount}
WHERE id = #{userId}
