mapper.insert:
INSERT INTO account (
    user_id, bank_name, account_number,
    product_name, interest_rate, pin_account_number,
    account_type, balance, connected_at
)
VALUES (
           #{userId}, #{bankName}, #{accountNumber},
           #{productName}, #{interestRate}, #{pinAccountNumber},
           #{accountType}, #{balance}, #{connectedAt}
       )

mapper.update:
UPDATE account
SET balance = #{balance, jdbcType=DECIMAL}
WHERE pin_account_number = #{pinAccountNumber}
