mapper.select:
SELECT
id, user_id, card_id, auth_number, sales_type, approved_at, payment_date,
amount, is_cancelled, cancel_amount, cancelled_at, merchant_name,
tpbcd, tpbcd_nm, installment_month, currency, foreign_amount, created_at
FROM card_transaction
WHERE user_id = #{userId}
AND card_id = #{cardId}
AND is_cancelled = FALSE
AND approved_at >= #{from}
AND approved_at <= #{to}
ORDER BY approved_at DESC

SELECT COUNT(1)
FROM card_transaction
WHERE user_id   = #{userId}
AND card_id   = #{cardId}
AND auth_number = #{authNumber}
AND approved_at = #{approvedAt, jdbcType=TIMESTAMP,
typeHandler=org.apache.ibatis.type.LocalDateTimeTypeHandler}

SELECT COALESCE(SUM(amount), 0)
FROM card_transaction
WHERE user_id = #{userId}
AND is_cancelled = FALSE
AND approved_at >= #{start}
AND approved_at <= #{end}

SELECT COALESCE(SUM(amount),0)
FROM card_transaction
WHERE user_id = #{userId}
  AND card_id = #{cardId}
  AND is_cancelled = FALSE
  AND approved_at >= #{start}
  AND approved_at <= #{end}
