mapper.select:
SELECT
l.id, l.user_id, l.source_type, l.source_name,
l.amount, l.type,
c.label AS category,  
l.date, l.merchant_name
FROM ledger l
JOIN tr_category c ON l.category_id = c.id
WHERE l.user_id = #{userId}
AND l.date >= #{from}
AND l.date <= #{to}
AND c.name = #{category}
ORDER BY l.date DESC
